import React, { useState, useEffect, useRef } from 'react';
import Lottie from "lottie-react";
import Loading from '../animations/Loadingskeleton.json';
import style from '../pages/AllProductsStyle/style.module.css';
import { motion, useAnimation } from 'framer-motion';
import RoundedButton from '../Components/common/RoundedButton/RoundedButton';
import { Link } from 'react-router-dom';
import FilterItems from './products/components/Filtering/Filter';
import { Skeleton } from '../Components/ui/skeleton';
import { XIcon } from 'lucide-react';

const apiUrl = process.env.REACT_APP_API_URL;
const words = "Everything:";

export default function AllProducts({ addItemToCart }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredTag, setFilteredTag] = useState(null);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const containerRef = useRef(null);
  const controls = useAnimation();
  const [searchQuery, setSearchQuery] = useState(''); 


  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data.data);
        setFilteredProducts(data.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setTimeout(() => {
          setLoading(false);
        }, 2000);
      }
    };

    fetchProducts();
  }, []);

  useEffect(() => {
    if (!loading) {
      controls.start("visible");
    }
  }, [loading, controls]);

  useEffect(() => {
    const startProductAnimations = async () => {
      for (let index = 0; index < filteredProducts.length; index++) {
        await controls.start(i => ({
          opacity: 1,
          transition: { delay: i * 0.1 }
        }));
      }
    };

    startProductAnimations();
  }, [filteredProducts, controls]);

  useEffect(() => {
    if (filteredTag !== null) {
      controls.start("visible");
    }
  }, [filteredTag, controls]);

  const handleTagSelect = (tag) => {
    if (tag === filteredTag) {
      // If the same tag is clicked twice, clear the filter
      setFilteredTag(null);
      setFilteredProducts(products);
      // Restart animation
      controls.start("visible");
    } else {
      // Apply the filter based on the selected tag
      const filtered = tag ? products.filter(product => product.tags.includes(tag)) : products;
      setFilteredTag(tag);
      setFilteredProducts(filtered);
    }
  };

  const searchedPosts = products.filter((data) =>{
    if (searchQuery) {
      const normalizedQuery = searchQuery.toLowerCase();
      return(
        (products.title?.toLowerCase().includes(normalizedQuery) || false) || 
        (products.body?.toLowerCase().includes(normalizedQuery) || false)
      );
    }

    return true;
  })

  return (
    <motion.div ref={containerRef} className={style.Container}>
      {loading ? (
        <div className={style.Loader}>
          <div className=' absolute top-[15%] left-[29%]'>
            <Skeleton className="bg-gray-400 h-12 w-[20rem] xl:w-[26rem]"/>

          </div>
        <div className=''>
        <Skeleton className="bg-gray-400 w-52 h-4 absolute top-[48%] left-[27%]"/>
        <Skeleton className="bg-gray-400 w-52 h-4 absolute top-[46%] left-[27%]"/>


        <Skeleton className="bg-gray-400 w-28 h-10 absolute top-[52%] left-[27%] rounded-full"/>
        <Skeleton className="bg-gray-400 w-28 h-10 absolute top-[52%] left-[34%] rounded-full"/>
        <Skeleton className="bg-gray-400 w-28 h-10 absolute top-[52%] left-[41%] rounded-full"/>
        </div>
          {[1, 2, 3].map((_, index) => (
            <motion.div 
              key={index} 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 0.5, delay: index * 0.1 + 1 }}
            >
              <Lottie animationData={Loading} width={20} height={20} style={{height: "500px", width: "auto", overflow: "auto"}}/>
            </motion.div>
          ))}
        </div>
      ) : (
        <div>
          <h1 className={style.BackgroundAnimation}>
            {filteredTag ? (
              <motion.span
                variants={{
                  visible: { opacity: 1, y: 0 },
                  hidden: { opacity: 0, y: 0 }
                }}
                initial="hidden"
                animate="visible"
              >
                {filteredTag}:
              </motion.span>
            ) : (
              words.split('').map((letter, index) => (
                <motion.span
                  key={index}
                  variants={{
                    visible: { opacity: 1, y: 0 },
                    hidden: { opacity: 0, y: 80 }
                  }}
                  initial="hidden"
                  animate={filteredTag === null ? "visible" : controls}
                  transition={{ delay: index * 0.1 }}
                >
                  {letter}
                </motion.span>
              ))
            )}
            <span>{filteredProducts.length}</span>
          </h1>

          <div className={style.FilterButtons}>
            <FilterItems onTagSelect={handleTagSelect} activeTag={filteredTag} />
            {filteredTag && (
            <div className={style.ClearFilter}>
            <RoundedButton backgroundColor="" onClick={() => {
              handleTagSelect(null);
              controls.start("visible");
            }}>All Products<XIcon/></RoundedButton>
          </div>
            )}
          </div>

          <div className={`${style.Products}`}>
            {filteredProducts.map((product, index) => (
              <motion.div
                key={product.id}
                className={`${style.Product} ${style.GridProduct}`}
                variants={{
                  visible: { opacity: 1, y: 0 },
                  hidden: { opacity: 0, y: 50 }
                }}
                initial="hidden"
                animate={controls}
                transition={{ delay: index * 0.1 }}
              >
                 <Link to={`/products/${product.id}`}>
                  <img src={product.image.url} alt={product.title} />
                  <h3>{product.description}</h3>
                  <p className={style.Old}>Price: ${product.price}</p>
                  <span>New Price</span>
                  <h2>${product.discountedPrice}</h2>
                  <div className={style.Button}>
                    <div>
                      <RoundedButton 
                        backgroundColor='#fff' 
                      >
                        <p>View Item</p>
                      </RoundedButton>
                    </div>
                  </div>
                </Link>
              </motion.div>
            ))}
          </div>
        </div>
      )}
    </motion.div>
  );
}
