import React, { useState, useEffect, useRef } from 'react';
import Lottie from "lottie-react";
import Loading from '../animations/Loadingskeleton.json';
import style from '../pages/Styles/style.module.css'
import { motion, useAnimation } from 'framer-motion';
import RoundedButton from '../Components/common/RoundedButton/RoundedButton';
const apiUrl = process.env.REACT_APP_API_URL;
const words = "All Items:";

export default function AllProducts() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Set initial loading state to true
  const containerRef = useRef(null); // Reference to the container div
  const controls = useAnimation(); // Framer Motion controls
  useEffect(() => {
    const startAnimation = () => {
      setTimeout(() => {
        setLoading(false);
        controls.start(i => ({
          opacity: 1,

          transition: { delay: i * 0.1 }
        }));
      }, 2000); // Delay of 2 seconds
    };

    if (loading) {
      startAnimation();
    }
  }, [setLoading, controls]);
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        console.log(data); // Log the response
        setProducts(data.data); // Assuming the products are under the 'data' key
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setTimeout(() => { // Add a setTimeout with a 1-second delay
          setLoading(false); // Set loading to false after the delay
          controls.start("visible"); // Start animation when loading completes
        }, 2000);  
      }
    };

    fetchProducts();
  }, [controls]);

  const history = useHistory(); // Get the history object from react-router-dom

  const handleButtonClick = (productId) => {
    // Navigate to the product-specific page with the product id in the URL
    history.push(`/products/${productId}`);
  };

  return (
    <motion.div ref={containerRef} className={style.Container}>
      {/* Animate the words and the h1 tag */}
      <motion.div
      initial={{ opacity: 0 }}
      animate={controls}
      className={style.BackgroundAnimation}
    >
      {/* Animate the word "Todays offers" */}
      {words.split('').map((letter, index) => (
        <motion.span
          key={index}
          initial={{ opacity: 0 }}
          animate={controls}
          custom={index}
        >
          {letter}
        </motion.span>
      ))}
    </motion.div>


      {loading ? (
        <div className={style.Loader}>
          {[1, 2, 3].map((_, index) => (
            <motion.div 
              key={index} 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 0.5, delay: index * 0.1 + 1 }} // staggered animation with 1 second delay
            >
              <Lottie animationData={Loading} width={20} height={20} style={{height: "500px", width: "auto", overflow: "auto"}}/>
            </motion.div>
          ))}
        </div>
      ) : (
        <div className={`${style.Products}`}>
          {/* Rendering only the first three products */}
          {products.map((product, index) => (
  <motion.div 
    key={product.id} 
    className={`${style.Product} ${style.GridProduct}`} /* Add appropriate class */
    variants={{
      visible: { opacity: 1, y: 0 },
      hidden: { opacity: 0, y: 50 }
    }}
    initial="hidden"
    animate={controls}
    transition={{ delay: index * 0.1 }} // staggered animation based on product index
  >
    <img src={product.image.url} alt={product.title} />
    <h3>{product.description}</h3>
    <p className={style.Old}>Price: ${product.price}</p>
    <span>New Price</span>
    <h2>${product.discountedPrice}</h2>
    <div className={style.Button}>
      <RoundedButton backgroundColor='#fff'
      onClick={() => {}}
      ><p>Add to cart</p></RoundedButton>
    </div>
  </motion.div>
))}
        </div>
      )}
    </motion.div>
  );
}
