import { ShoppingCartIcon, XIcon } from 'lucide-react';
import React, { useState, useEffect } from 'react';
import style from './style.module.css';

function ShoppingCart({ cartItems }) {
  const [isOpen, setIsOpen] = useState(false);
  const [products, setProducts] = useState([]);

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  // Fetch product details when the component mounts
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productDetails = await Promise.all(cartItems.map(async (productId) => {
          const response = await fetch(`https://v2.api.noroff.dev/online-shop/${productId}`);
          if (!response.ok) {
            throw new Error('Failed to fetch product details');
          }
          const productData = await response.json();
          return { data: productData.data, quantity: 1 };
        }));
        setProducts(productDetails);
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    fetchProducts();
  }, []); // Empty dependency array to fetch products only once when the component mounts

  // Update cart items when cartItems change
  useEffect(() => {
    const updateCart = () => {
      const updatedProducts = [...products];
      cartItems.forEach((productId) => {
        const existingProduct = updatedProducts.find((product) => product.data.id === productId);
        if (existingProduct) {
          existingProduct.quantity++;
        } else {
          // Fetch product details for newly added cart items
          fetchProductDetails(productId);
        }
      });
      setProducts(updatedProducts);
    };

    updateCart();
  }, [cartItems]); // Update cart items when cartItems change

  // Fetch product details for newly added cart items
  const fetchProductDetails = async (productId) => {
    try {
      const response = await fetch(`https://v2.api.noroff.dev/online-shop/${productId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch product details');
      }
      const productData = await response.json();
      setProducts((prevProducts) => [...prevProducts, { data: productData.data, quantity: 1 }]);
    } catch (error) {
      console.error('Error fetching product details:', error);
    }
  };

  // Calculate total number of items in cart
  const totalItemsInCart = products.reduce((total, product) => {
    return total + product.quantity;
  }, 0);

  return (
    <div className="">
      <div onClick={toggleCart} className={style.openClose}>
        {isOpen ? <XIcon /> : <div>
          <ShoppingCartIcon />
          <p>{totalItemsInCart}</p>
        </div>}
      </div>
      {isOpen && (
        <div className={style.CartContainer}>
          <h2>Shopping Cart</h2>
          <p>Total Items: {totalItemsInCart}</p>
          <ul className=''>
            {products.map((product, index) => (
              <li key={index} className={style.CartItems}>
                <XIcon />
                <span>{product.data.title}</span>
                <span>${product.data.price}</span>
                <span>Quantity: {product.quantity}</span>
                {product.data.image && product.data.image.url && (
                  <img src={product.data.image.url} alt={product.data.title} />
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShoppingCart;
