import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Hero from './Components/Hero';
import Products from './Components/Products';
import AllProducts from './pages/AllProducts'; // Import the AllProducts component
import { AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';
import Preloader from '../src/Components/Preloader/index';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default';
          window.scrollTo(0, 0);
        }, 1000);
      }
    )();
  }, []);

  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>

        <Hero />
        <Products />

        <Routes>
          <Route path="/AllProducts" element={<AllProducts />} /> {/* Render AllProducts component when URL matches /AllProducts */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
