import React from 'react';
import { Link } from 'react-router-dom';
import RoundedButton from '../common/RoundedButton/RoundedButton';
import style from './style.module.css';
import { motion, useAnimation } from 'framer-motion';

export default function Hero() {
  const gifUrl = "https://gifdb.com/images/high/cold-foggy-mountain-rbvi7wvg05uz8m2f.webp";

  return (
    <div className={style.Hero}>
      <motion.div className={style.Button}

       initial={{ opacity: 0 }} // Set the initial opacity to 0
       animate={{ opacity: 1 }} // Animate the opacity to 1
       transition={{ duration: 5.5 }} // Set the transition duration
       >
        <Link to="/AllProducts"> {/* Use Link to navigate to All Products page */}
          <RoundedButton backgroundColor={"#FFF"}>
            <p>View all</p>
          </RoundedButton>
        </Link>
      </motion.div>
    </div>
  );
}
