// FilterItems.js
import React from "react";
import style from './style.module.css';
import RoundedButton from "../../../../../Components/common/RoundedButton/RoundedButton";

export default function FilterItems({ onTagSelect, activeTag }) {
  const handleTagSelect = (tag) => {
    // Apply the filter based on the selected tag
    const filtered = tag ? products.filter(product => product.tags.includes(tag)) : products;
    setFilteredTag(tag);
    setFilteredProducts(filtered);
  };

  return (
    <div className={style.FilterContainer}>
      <RoundedButton backgroundColor="" onClick={() => handleTagSelect("electronics")} active={activeTag === "electronics" ? true : false}>{activeTag === "electronics" ? "Electronics" : "All"}</RoundedButton>
      <RoundedButton backgroundColor="" onClick={() => handleTagSelect("beauty")} active={activeTag === "beauty" ? true : false}>{activeTag === "beauty" ? "Beauty" : "All"}</RoundedButton>
      <RoundedButton backgroundColor="" onClick={() => handleTagSelect("fashion")} active={activeTag === "fashion" ? true : false}>{activeTag === "fashion" ? "Fashion" : "All"}</RoundedButton>
    </div>
  );
}
