export default function HpProducts() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const containerRef = useRef(null);
  const letterControls = useRef([]); // Create ref for animation controls for each letter

  useEffect(() => {
    // Initialize animation controls for each letter
    letterControls.current = Array.from({ length: words.length }).map(() => useAnimation());

    const startAnimation = async () => {
      setLoading(true);
      // Start animations for each letter
      letterControls.current.forEach((control, index) => {
        control.start({ opacity: 1, transition: { delay: index * 0.1 } });
      });
    };

    if (loading) {
      startAnimation();
    }
  }, [loading]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        console.log(data);
        setProducts(data.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setTimeout(() => {
          setLoading(false);
        }, 2000);
      }
    };

    fetchProducts();
  }, []);

  return (
    <motion.div ref={containerRef} className={style.Container}>
      <motion.div initial={{ opacity: 0 }} className={style.BackgroundAnimation}>
        {/* Animate each letter of the word "Today's offers" individually */}
        {words.split('').map((letter, index) => (
          <motion.span
            key={index}
            initial={{ opacity: 0 }}
            animate={letterControls.current[index]} // Use individual animation controls for each letter
            custom={index}
          >
            {letter}
          </motion.span>
        ))}
      </motion.div>

      {loading ? (
        <div className={style.Loader}>
          {[1, 2, 3].map((_, index) => (
            <motion.div 
              key={index} 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 0.5, delay: index * 0.1 + 1 }}
            >
              <Lottie animationData={Loading} width={20} height={20} style={{height: "500px", width: "auto", overflow: "auto"}}/>
            </motion.div>
          ))}
        </div>
      ) : (
        <div className={style.Products}>
          {products.slice(0, 3).map((product, index) => (
            <motion.div 
              key={product.id} 
              className={style.Product}
              variants={{
                visible: { opacity: 1, y: 0 },
                hidden: { opacity: 0, y: 50 }
              }}
              initial="hidden"
              animate={{ opacity: 1, y: 0 }} // Animate products independently
              transition={{ delay: index * 0.1 }}
            >
              <img src={product.image.url} alt={product.title} />
              <h3>{product.description}</h3>
              <p className={style.Old}>Price: ${product.price}</p>
              <span>New Price</span>
              <h2>${product.discountedPrice}</h2>
              <div className={style.Button}>
                <RoundedButton backgroundColor='#fff'><p>Add to cart</p></RoundedButton>
              </div>
            </motion.div>
          ))}
        </div>
      )}
    </motion.div>
  );
}
