import './App.css';
import Nav from './Components/Nav';
import Hero from './Components/Hero';
import Products from './Components/Products';
import AllProducts from './pages/AllProducts'; // Import the AllProducts component
import { BrowserRouter as Router, Route } from 'react-router-dom';
import Switch from 'react-router-dom/Switch';
import { AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';
import Preloader from '../src/Components/Preloader/index';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default'
          window.scrollTo(0, 0);
        }, 1000)
      }
    )()
  }, [])

  return (
    <Router> {/* Wrap your app with BrowserRouter */}
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>

        <Nav />
  {/* Use Switch to render only the first matching route */}
          <Route exact path="/" component={Hero} /> {/* Define your routes */}
          <Route exact path="/products" component={Products} />
          <Route exact path="/all-products" component={AllProducts} /> {/* Add the route for AllProducts */}

      </div>
    </Router>
  );
}

export default App;
