import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function ProductDetails() {
  const { id } = useParams(); // Use 'id' to match the parameter in the route
  console.log("id:", id); // Log the value of 'id' to check if it's correctly retrieved
  const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const url = `https://v2.api.noroff.dev/online-shop/${id}`; // Use the correct API endpoint with the 'id' parameter
        const response = await fetch(url);
        const data = await response.json();
        setProduct(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setIsLoading(false);
      }
    };
    console.log('Data:', data);
console.log('Product:', product);


    fetchProduct();
  }, [id]); // Use 'id' in the dependency array

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div>
      <h2>{product.title}</h2>
      <p>{product.description}</p>
      <p>Price: {product.price}</p>
      {/* Render other product details as needed */}
    </div>
  );
}

export default ProductDetails;
