// AllProducts.js
import React, { useState, useEffect, useRef } from 'react';
import Lottie from "lottie-react";
import Loading from '../animations/Loadingskeleton.json';
import style from '../pages/AllProductsStyle/style.module.css';
import { motion, useAnimation } from 'framer-motion';
import RoundedButton from '../Components/common/RoundedButton/RoundedButton';
import { Link } from 'react-router-dom';
import FilterItems from './products/[product.id]/components/Filtering/Filter';

const apiUrl = process.env.REACT_APP_API_URL;
const words = "All Items:";

export default function AllProducts({ addItemToCart }) {
  const [products, setProducts] = useState([]); // Initialize products state with null
  const [loading, setLoading] = useState(true);
  const [filteredTag, setFilteredTag] = useState(null); // State to hold the selected tag for filtering
  const [filteredProducts, setFilteredProducts] = useState([]); // State to hold filtered products
  const containerRef = useRef(null);
  const controls = useAnimation();

  useEffect(() => {
    const startAnimation = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data.data);
        setFilteredProducts(data.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setLoading(false);
        controls.start(i => ({
          opacity: 1,
          transition: { delay: i * 0.1 }
        }));
      }
    };

    startAnimation();
  }, [controls]);

  useEffect(() => {
    const startProductAnimations = () => {
      filteredProducts.forEach((_, index) => {
        controls.start(i => ({
          opacity: 1,
          transition: { delay: i * 0.1 }
        }));
      });
    };

    startProductAnimations();
  }, [filteredProducts, controls]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data.data); // Set products state with fetched data
        setFilteredProducts(data.data); // Initialize filtered products with all products
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setTimeout(() => {
          setLoading(false);
          controls.start("visible");
        }, 2000);  
      }
    };

    fetchProducts();
  }, [controls]);

  const handleTagSelect = (tag) => {
    // If the same tag is clicked twice, clear the filter
    if (tag === filteredTag) {
      setFilteredTag(null);
      setFilteredProducts(products); // Reset to all products
    } else {
      // Apply the filter based on the selected tag
      const filtered = tag ? products.filter(product => product.tags.includes(tag)) : products;
      setFilteredTag(tag);
      setFilteredProducts(filtered);
    }
  };

  // Log the number of products being rendered
  console.log("Number of products:", filteredProducts.length);

  return (
    <motion.div ref={containerRef} className={style.Container}>
      {loading ? (
        <div className={style.Loader}>
          {[1, 2, 3].map((_, index) => (
            <motion.div 
              key={index} 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 0.5, delay: index * 0.1 + 1 }}
            >
              <Lottie animationData={Loading} width={20} height={20} style={{height: "500px", width: "auto", overflow: "auto"}}/>
            </motion.div>
          ))}
        </div>
      ) : (
        <div>
          <h1 className={style.BackgroundAnimation}>
            {words.split('').map((letter, index) => (
              <motion.span
                key={index}
                variants={{
                  visible: { opacity: 1, y: 0 },
                  hidden: { opacity: 0, y: 50 }
                }}
                initial="hidden"
                animate={controls}
                transition={{ delay: index * 0.1 }}
              >
                {letter}
              </motion.span>
            ))}
          </h1>

          {/* Pass the handleTagSelect function and filteredTag as props */}
          <FilterItems onTagSelect={handleTagSelect} activeTag={filteredTag} />

          {/* Render clear filter button if a filter tag is selected */}
          {filteredTag && (
            <div className={style.ClearFilter}>
              <RoundedButton backgroundColor="" onClick={() => handleTagSelect(null)}>Clear Filter</RoundedButton>
            </div>
          )}

          <div className={`${style.Products}`}>
            {filteredProducts.map((product, index) => (
              <motion.div
                key={product.id}
                className={`${style.Product} ${style.GridProduct}`}
                variants={{
                  visible: { opacity: 1, y: 0 },
                  hidden: { opacity: 0, y: 50 }
                }}
                initial="hidden"
                animate={controls}
                transition={{ delay: index * 0.1 }}
              >
                <Link to={`/products/${product.id}`}>
                  <img src={product.image.url} alt={product.title} />
                  <h3>{product.description}</h3>
                  <p className={style.Old}>Price: ${product.price}</p>
                  <span>New Price</span>
                  <h2>${product.discountedPrice}</h2>
                  <div className={style.Button}>
                    <div>
                      <RoundedButton 
                        backgroundColor='#fff' 
                      >
                        <p>View Item</p>
                      </RoundedButton>
                    </div>
                  </div>
                </Link>
              </motion.div>
            ))}
          </div>
        </div>
      )}
    </motion.div>
  );
}
