// ProductDetails.js
import RoundedButton from '../../../Components/common/RoundedButton/RoundedButton';
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

import style from './styles/style.module.css'

function ProductDetails({ addItemToCart }) {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const url = `https://v2.api.noroff.dev/online-shop/${id}`;
        const response = await fetch(url);
        const data = await response.json();
        setProduct(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    if (product) {
      addItemToCart(product);
    }
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className={style.ProductContainer}>
      <div key={product.data.id}>
        <img src={product.data.image.url} alt={product.title} />
        <h2>{product.data.title}</h2>
        <p>{product.data.description}</p>
        <p>Price: {product.data.price}</p>
        <div>
          <RoundedButton backgroundColor='#fff' onClick={handleAddToCart} type="submit">
            <p>Add to cart</p>
            </RoundedButton>
        </div>

      </div>
    </div>
  );
}

export default ProductDetails;
