// FilterItems.js
import React from "react";
import style from './style.module.css';
import RoundedButton from "../../../../../Components/common/RoundedButton/RoundedButton";

export default function FilterItems({ onTagSelect, activeTag }) {
  const handleTagSelect = (tag) => {
    onTagSelect(tag);
  };

  return (
    <div className={style.FilterContainer}>
      <RoundedButton backgroundColor="" onClick={() => handleTagSelect("electronics")} active={activeTag === "electronics"}><p>Electronics</p></RoundedButton>
      <RoundedButton backgroundColor="" onClick={() => handleTagSelect("beauty")} active={activeTag === "beauty"}><p>Beauty</p></RoundedButton>
      <RoundedButton backgroundColor="" onClick={() => handleTagSelect("fashion")} active={activeTag === "fashion"}><p>Fashion</p></RoundedButton>
    </div>
  );
}
