import RoundedButton from '../../../Components/common/RoundedButton/RoundedButton';
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ProductReviews from './components/ProductReviews/ProductReviews'; // Import ProductReviews component

import style from './styles/style.module.css';

function ProductDetails({ addItemToCart, cartItems }) {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const url = `https://v2.api.noroff.dev/online-shop/${id}`;
        const response = await fetch(url);
        const data = await response.json();
        setProduct(data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setIsLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    if (product) {
      const existingProductIndex = cartItems.findIndex(item => item.id === product.data.id);
      if (existingProductIndex !== -1) {
        // Product already exists in the cart, update quantity
        const updatedCartItems = cartItems.map((item, index) => {
          if (index === existingProductIndex) {
            return { ...item, quantity: item.quantity + 1 };
          }
          return item;
        });
        addItemToCart(updatedCartItems);
      } else {
        // Product doesn't exist in the cart, add it
        addItemToCart([...cartItems, { id: product.data.id, data: product.data, quantity: 1 }]);
      }
    } 
  };


  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div >
      <div className={style.ProductContainer} key={product.data.id}>
        <img src={product.data.image.url} alt={product.data.title} />
        <div className={style.col2}>
          <h2>{product.data.title}</h2>
          <p>{product.data.description}</p>
          <p>Price: {product.data.price}</p>
          <p>Price: {product.data.discountedPrice}</p>
          <div>
            <RoundedButton backgroundColor='#fff' onClick={handleAddToCart} type="submit">
              <p>Add to cart</p>
            </RoundedButton>
          </div>
        </div>
      </div>
      <hr/>
      {/* Render ProductReviews component and pass reviews array as prop */}
      <ProductReviews reviews={product.data.reviews} />
    </div>
  );
}

export default ProductDetails;
