import React, { useState, useEffect } from 'react';
import { ShoppingCartIcon, XIcon, PlusIcon, MinusIcon } from 'lucide-react';
import style from './style.module.css';
import RoundedButton from '../common/RoundedButton/RoundedButton';

function ShoppingCart({ cartItems, addItemToCart }) {
  const [isOpen, setIsOpen] = useState(false);
  const [products, setProducts] = useState([]);

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productDetails = await Promise.all(
          cartItems.map(async (productId) => {
            const response = await fetch(`https://v2.api.noroff.dev/online-shop/${productId}`);
            if (!response.ok) {
              throw new Error('Failed to fetch product details');
            }
            const productData = await response.json();
            return { id: productId, data: productData.data, quantity: 1 };
          })
        );
        setProducts(productDetails);
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    fetchProducts();
  }, [cartItems]);

  const addToCart = (productId, productData) => {
    const existingProductIndex = products.findIndex((p) => p.id === productId);

    if (existingProductIndex !== -1) {
      // Product already exists in the cart, update quantity
      setProducts((prevProducts) =>
        prevProducts.map((p, index) =>
          index === existingProductIndex ? { ...p, quantity: p.quantity + 1 } : p
        )
      );
    } else {
      // Product doesn't exist in the cart, add it
      setProducts((prevProducts) => [
        ...prevProducts,
        { id: productId, data: productData, quantity: 1 },
      ]);
    }
  };




  const removeFromCart = (productId) => {
    const productIndex = products.findIndex((product) => product.id === productId);
    if (productIndex !== -1 && products[productIndex].quantity > 1) {
      // If product quantity is greater than 1, decrease it
      setProducts((prevProducts) => {
        const updatedProducts = [...prevProducts];
        updatedProducts[productIndex].quantity -= 1;
        return updatedProducts;
      });
    } else {
      // If product quantity is 1 or product not found, remove it from the cart
      setProducts((prevProducts) => prevProducts.filter((product) => product.id !== productId));
    }
  };

  // Calculate total number of items in cart
  const totalItemsInCart = products.reduce((total, product) => {
    return total + product.quantity;
  }, 0);

  return (
    <div className="">
      <div onClick={toggleCart} className={style.openClose}>
        {isOpen ? (
          <XIcon />
        ) : (
          <div>
            <ShoppingCartIcon />
            <p>{totalItemsInCart}</p>
          </div>
        )}
      </div>
      {isOpen && (
        <div className={style.CartContainer}>
          <h2>Shopping Cart</h2>
          <p>Total Items: {totalItemsInCart}</p>
          <ul className="">
            {products.map((product, index) => (
              <li key={index} className={style.CartItems}>
                <PlusIcon onClick={() => addToCart(product)} />{/* PlusIcon button */}
                <MinusIcon onClick={() => removeFromCart(product.id)} /> {/* MinusIcon button */}
                <span>{product.data.title}</span>
                <span>${product.data.price}</span>
                <span>Quantity: {product.quantity}</span>
                {product.data.image && product.data.image.url && (
                  <img src={product.data.image.url} alt={product.data.title} />
                )}
              </li>
            ))}
            <RoundedButton>
              <p>Go to checkOut</p>
            </RoundedButton>
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShoppingCart;
