import React, { useState, useEffect, useRef, useLayoutEffect } from 'react';
import Lottie from "lottie-react";
import Loading from '../../animations/Loadingskeleton.json'
import style from './style.module.scss';
import RoundedButton from '../common/RoundedButton/RoundedButton';
import { motion } from 'framer-motion';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import ScrollToPlugin from 'gsap/ScrollToPlugin';

export default function Products() {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const containerRef = useRef(null);
    const titleRef = useRef(null);
    const [isMobile, setIsMobile] = useState(false);


    const background = useRef(null);
    const introImage = useRef(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch("https://v2.api.noroff.dev/online-shop");
                if (!response.ok) {
                    throw new Error('Failed to fetch products');
                }
                const data = await response.json();
                setProducts(data.data);
            } catch (error) {
                console.error('Error fetching products:', error);
            } finally {
                setLoading(false);
            }
        };
        fetchProducts();
    }, []);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 1000);
        };

        // Initial check
        handleResize();

        // Add event listener to update on resize
        window.addEventListener('resize', handleResize);

        // Cleanup
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useLayoutEffect(() => {
      gsap.registerPlugin(ScrollTrigger);

      const timeline = gsap.timeline({
          scrollTrigger: {
              trigger: document.documentElement,
              scrub: true,
              start: "top",
              end: "+=500px",

          },
      })

      timeline
          .from(background.current, { clipPath: `inset(20%)` })
          .to(introImage.current, { height: "200px" }, 0)
  }, [])


  gsap.registerPlugin(ScrollToPlugin);

    return (
        <div className={style.Container} ref={containerRef}>
            <motion.div>
                <svg viewBox="0 0 100 100" width="100" height="100">
                    <defs>
                        <path id="circle"
                            d="
                            M 50, 50
                            m -37, 0
                            a 37,37 0 1,1 74,0
                            a 37,37 0 1,1 -74,0"/>
                    </defs>
                    <text fontSize="17">
                        <textPath href="#circle">
                            <motion.h1 ref={titleRef}>Todays special offers...</motion.h1>
                        </textPath>
                    </text>
                </svg>

                {loading ? (
                    <div className={style.Loader}>
                        <Lottie animationData={Loading} width={20} height={20} style={{ height: "300px", width: "auto", overflow: "auto" }} />
                        <Lottie animationData={Loading} width={20} height={20} style={{ height: "300px", width: "auto", overflow: "auto" }} />
                        <Lottie animationData={Loading} width={20} height={20} style={{ height: "300px", width: "auto", overflow: "auto" }} />
                    </div>
                ) : (
                    <div className={style.Products} >
                        {products.slice(0, 3).map((product, index) => (
                            <div key={product.id} className={`${style.Product} product`}>
                                <img src={product.image.url} alt={product.title} data-scroll data-scroll-speed="0.3" />
                                <h1>{product.title}</h1>
                                <h3>{product.description}</h3>
                                <p className={style.Old}>Price: ${product.price}</p>
                                <span>New Price</span>
                                <h2>{product.discountedPrice}</h2>
                                <div className={style.Button}>
                                    <RoundedButton><p>Add to cart</p></RoundedButton>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </motion.div>
        </div>
    );
}
