import { ShoppingCartIcon, XIcon } from 'lucide-react';
import React, { useState, useEffect } from 'react';
import style from './style.module.css';

function ShoppingCart({ cartItems }) {
  const [isOpen, setIsOpen] = useState(false);
  console.log(cartItems);


  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  // Calculate total number of items in cart
  const totalItemsInCart = cartItems ? cartItems.length : 0;

  return (
    <div className="">
      <div onClick={toggleCart} className={style.openClose}>
        {isOpen ? <XIcon /> : <div>
          <ShoppingCartIcon />
          <p>{totalItemsInCart}</p> {/* Use totalItemsInCart instead of cartItems.length */}
        </div>}
      </div>
      {isOpen && (
        <div className={style.CartContainer}>
          <h2>Shopping Cart</h2>
          <p>Total Items: {totalItemsInCart}</p>
          <ul className=''>
            {cartItems.map((item, index) => (
              <li key={index} className={style.CartItems}>
                <XIcon />
                {item.data && item.data.title && <span>{item.data.title}</span>}
                {item.data && item.data.price && <span>${item.data.price}</span>}
                {item.data && item.data.image && <img src={item.data.image.url} alt={item.data.title} />}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShoppingCart;
