import React, { useState, useEffect } from 'react';
import { ShoppingCartIcon, XIcon, PlusIcon, MinusIcon } from 'lucide-react';
import style from './style.module.css';
import RoundedButton from '../common/RoundedButton/RoundedButton';

function ShoppingCart({ cartItems }) {

  const [isOpen, setIsOpen] = useState(false);

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  // Calculate total number of items in cart
  const totalItemsInCart = cartItems.reduce((total, item) => {
    return total + item.quantity;
  }, 0);

  const addToCart = (productId) => {
    console.log('Adding product to cart:', productId);
    const updatedCartItems = cartItems.map(item => {
      if (item.productId === productId) {
        return { ...item, quantity: item.quantity + 1 };
      }
      return item;
    });
    // Call the parent component's function to update cartItems
    // Assuming you have a function to update cartItems in the parent component
    // For example, it could be called 'updateCartItems'
    updateCartItems(updatedCartItems);
  };

  const removeFromCart = (productId) => {
    console.log('Removing product from cart:', productId);
    const updatedCartItems = cartItems.map(item => {
      if (item.productId === productId && item.quantity > 1) {
        return { ...item, quantity: item.quantity - 1 };
      }
      return item;
    }).filter(item => item.productId !== productId);
    // Call the parent component's function to update cartItems
    // Assuming you have a function to update cartItems in the parent component
    // For example, it could be called 'updateCartItems'
    updateCartItems(updatedCartItems);
  };

  return (
    <div className="">
      <div onClick={toggleCart} className={style.openClose}>
        {isOpen ? (
          <XIcon />
        ) : (
          <div>
            <ShoppingCartIcon />
            <p>{totalItemsInCart}</p>
          </div>
        )}
      </div>
      {isOpen && (
        <div className={style.CartContainer}>
          <h2>Shopping Cart</h2>
          <p>Total Items: {totalItemsInCart}</p>
          <ul className="">
            {cartItems.map((item, index) => (
              <li key={index} className={style.CartItems}>
                <button onClick={() => addToCart(item.productId)}>
                  <PlusIcon />
                </button>
                <button onClick={() => removeFromCart(item.productId)}>
                  <MinusIcon />
                </button>
                <span>{item.productData.title}</span>
                <span>${item.productData.price}</span>
                <span>Quantity: {item.quantity}</span>
                {item.productData.image && item.productData.image.url && (
                  <img src={item.productData.image.url} alt={item.productData.title} />
                )}
              </li>
            ))}
            <RoundedButton>
              <p>Go to checkOut</p>
            </RoundedButton>
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShoppingCart;
