import React, { useState } from 'react';
import style from './CheckOutStyle/style.module.css'
function CheckOut({ cartItems }) {
  const [productPrice, setProductPrice] = useState(false)
  const totalItemsInCart = cartItems.reduce((total, item) => {
    return total + item.quantity;
  }, 0);

  return (
    <div className={style.Container}>
      <h1>Checkout</h1>
      <p>Total Items in Cart: {totalItemsInCart}</p>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            <h2>{item.productData.title}</h2>
            <span>${item.productData.discountedPrice}</span>
            <span>Quantity: {item.quantity}</span>
            {item.productData.image && item.productData.image.url && (
              <img src={item.productData.image.url} alt={item.productData.title} />
            )}
          </li>


        ))}
      </ul>
    </div>
  );
}

export default CheckOut;
