import React, { useState, useEffect, useRef } from 'react';
import Lottie from "lottie-react";
import Loading from '../../animations/Loadingskeleton.json'
import style from './style.module.css';
import RoundedButton from '../common/RoundedButton/RoundedButton';
import Lenis from '@studio-freight/lenis';
import { motion, useScroll, useTransform } from 'framer-motion';
const apiUrl = process.env.REACT_APP_API_URL;

export default function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Set initial loading state to true
  const containerRef = useRef(null); // Reference to the container div

  const container = useRef(null);
  const images = [Picture1, Picture2, Picture3];
  const lettersRef = useRef([])
  const imagesRef = useRef([])
  const title1 = useRef(null);
  useLayoutEffect( () => {
      const context = gsap.context( () => {
          const tl = gsap.timeline({
              scrollTrigger: {
                  trigger: container.current,
                  start: "top bottom",
                  end: "bottom top",
                  scrub: true,
              },
          })
          .to(title1.current, {y: -50}, 0)
          .to(imagesRef.current[1], {y: -150}, 0)
          .to(imagesRef.current[2], {y: -255}, 0)
          lettersRef.current.forEach((letter, i) => {
              tl.to(letter, {
                  top: Math.floor(Math.random() * -75) - 25,
              }, 0)
          })

      })
      return () => context.revert();
  }, [])

  return (
    <div className={style.Container}>
          <motion.div >
      <svg viewBox="0 0 100 100" width="100" height="100">
        <defs>
          <path id="circle"
            d="
              M 50, 50
              m -37, 0
              a 37,37 0 1,1 74,0
              a 37,37 0 1,1 -74,0"/>
        </defs>
        <text fontSize="17">
          <motion.h1></motion.h1>
          <textPath href="#circle" >
          <motion.h1>   Todays special offers...</motion.h1>

          </textPath>
        </text>
      </svg>

      {loading ? (
        <div className={style.Loader}>
          <Lottie animationData={Loading} width={20} height={20} style={{height: "300px", width: "auto", overflow: "auto"}}/>
          <Lottie animationData={Loading} width={20} height={20} style={{height: "300px", width: "auto", overflow: "auto"}}/>
          <Lottie animationData={Loading} width={20} height={20} style={{height: "300px", width: "auto", overflow: "auto"}}/>
        </div>
      ) : (
        <div className={style.Products}>
          {/* Rendering only the first three products */}
          {products.slice(0, 3).map((product, index) => (
            <div 
              key={product.id} 
              className={style.Product}
            >
              <img src={product.image.url} alt={product.title} />
              <h1 style={{md}}>{product.title}</h1>
              <h3>{product.description}</h3>
              <p className={style.Old}>Price: ${product.price}</p>
              <span>New Price</span>
              <h2>{product.discountedPrice}</h2>
              <div className={style.Button}>
                <RoundedButton><p>Add to cart</p></RoundedButton>
              </div>
            </div>
          ))}
        </div>

      )}
      </motion.div>
    </div>
  );
}
