// App.js

import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Hero from './Components/Hero';
import HpProducts from './Components/HpProducts/HpProducts';
import AllProducts from './pages/AllProducts'; // Import the AllProducts component
import NotFound from './pages/NotFound';
import { AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';
import Preloader from '../src/Components/Preloader/index';
import Nav from './Components/Nav';
import ProductDetails from '../src/pages/products/[product.id]'

function HomePage() {
  return (
    <>
      <Hero />
      <HpProducts />
    </>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default';
          window.scrollTo(0, 0);
        }, 1000);
      }
    )();
  }, []);

  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>
        <Nav />
        <Routes>
          {/* Render HomePage component for homepage */}
          <Route path="/" element={<HomePage />} />
          {/* Render AllProducts component */}
          <Route path="/AllProducts" element={<AllProducts />} />
          {/* Render ProductDetails component for product-specific pages */}
          <Route path="/products/:productId" element={<ProductDetails />} />
          {/* Render NotFound component for any other path */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
