import { ShoppingCartIcon, XIcon } from 'lucide-react';
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import style from './style.module.css';

function ShoppingCart({ cartItems }) {
  const [isOpen, setIsOpen] = useState(false);
  const [product, setProduct] = useState(null);

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  // Fetch product details based on the product ID from route parameters
  const fetchProductDetails = async (productId) => {
    try {
      const response = await fetch(`https://v2.api.noroff.dev/online-shop/${productId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch product details');
      }
      const productData = await response.json();
      setProduct(productData);
    } catch (error) {
      console.error('Error fetching product details:', error);
    }
  };

  // UseParams hook to get the product ID from route parameters
  const { productId } = useParams();

  useEffect(() => {
    if (productId) {
      fetchProductDetails(productId);
    }
  }, [productId]);

  // Calculate total number of items in cart
  const totalItemsInCart = cartItems ? cartItems.length : 0;

  return (
    <div className="">
      <div onClick={toggleCart} className={style.openClose}>
        {isOpen ? <XIcon /> : <div>
          <ShoppingCartIcon />
          <p>{totalItemsInCart}</p>
        </div>}
      </div>
      {isOpen && (
        <div className={style.CartContainer}>
          <h2>Shopping Cart</h2>
          <p>Total Items: {totalItemsInCart}</p>
          <ul className=''>
            {product && ( // Render product details if product is available
              <li className={style.CartItems}>
                <XIcon />
                <span>{product.title}</span>
                <span>${product.price}</span>
                <img src={product.image.url} alt={product.title} />
              </li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShoppingCart;
