import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Hero from './Components/Hero';
import HpProducts from './Components/HpProducts/HpProducts';
import AllProducts from './pages/AllProducts';
import NotFound from './pages/NotFound';
import { AnimatePresence } from 'framer-motion';
import Preloader from '../src/Components/Preloader/index';
import Nav from './Components/Nav';
import ProductDetails from './pages/products/[product.id]';
import ShoppingCart from './Components/Cart/ShoppingCart';
import CheckOut from './pages/CheckOut';

function HomePage() {
  return (
    <>
      <Hero />
      <HpProducts />
    </>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [products, setProducts] = useState([]); // State to store products

  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default';
          window.scrollTo(0, 0);
        }, 1000);
      }
    )();
  }, []);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data.data); // Set products state with fetched data
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addItemToCart = (productId) => {
    const existingProductIndex = cartItems.findIndex(item => item.id === productId);
    if (existingProductIndex !== -1) {
      // Product already exists in the cart, update quantity
      const updatedCartItems = [...cartItems];
      updatedCartItems[existingProductIndex].quantity += 1;
      setCartItems(updatedCartItems);
    } else {
      // Product doesn't exist in the cart, add it with quantity 1
      const productToAdd = products.find(product => product.id === productId);
      if (productToAdd) {
        setCartItems([...cartItems, { ...productToAdd, quantity: 1 }]);
      }
    }
  };

  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>
        <Nav addItemToCart={addItemToCart} cartItems={cartItems} />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/AllProducts" element={<AllProducts products={products} addItemToCart={addItemToCart} />} cartItems={cartItems} />
          <Route path="/products/:id" element={<ProductDetails addItemToCart={addItemToCart} cartItems={cartItems} />} />
          <Route path="/CheckOut" element={<CheckOut cartItems={cartItems} />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
