import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Hero from './Components/Hero';
import HpProducts from './Components/HpProducts/HpProducts';
import AllProducts from './pages/AllProducts';
import NotFound from './pages/NotFound';
import { AnimatePresence } from 'framer-motion';
import { useEffect } from 'react';
import Preloader from '../src/Components/Preloader/index';
import Nav from './Components/Nav';
import ProductDetails from './pages/products/[product.id]';
import ShoppingCart from './Components/Cart/ShoppingCart';
import CheckOut from './pages/CheckOut'; // Import the ViewCart component

function HomePage() {
  return (
    <>
      <Hero />
      <HpProducts />
    </>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [products, setProducts] = useState([]);
  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default';
          window.scrollTo(0, 0);
        }, 1000);
      }
    )();
  }, []);

  const addItemToCart = (productId, productData) => {
    console.log('Adding product to cart:', productId, productData);
    const existingProductIndex = products.findIndex((p) => p.id === productId);

    if (existingProductIndex !== -1) {
      // Product already exists in the cart, update quantity
      console.log('Product already exists in cart, updating quantity');
      setProducts((prevProducts) =>
        prevProducts.map((p, index) =>
          index === existingProductIndex ? { ...p, quantity: p.quantity + 1 } : p
        )
      );
    } else {
      // Product doesn't exist in the cart, add it
      console.log('Product not in cart, adding to cart');
      setProducts((prevProducts) => [
        ...prevProducts,
        { id: productId, data: productData, quantity: 1 },
      ]);
    }
  };



  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>
        <Nav addItemToCart={addItemToCart} cartItems={cartItems} />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/AllProducts" element={<AllProducts addItemToCart={addItemToCart} />} />
          <Route path="/products/:id" element={<ProductDetails addItemToCart={addItemToCart} />} />

          <Route path="/CheckOut" element={<CheckOut cartItems={cartItems} />} /> {/* Add route for ViewCart */}
          <Route path="*" element={<NotFound />} />
        </Routes>

      </div>
    </Router>
  );
}

export default App;
