import React, { useState, useEffect } from 'react';
import { ShoppingCartIcon, XIcon, PlusIcon, MinusIcon } from 'lucide-react';
import style from './style.module.css';
import RoundedButton from '../common/RoundedButton/RoundedButton';

function ShoppingCart({ cartItems }) {

  const [products, setProducts] = useState([]);




  const [isOpen, setIsOpen] = useState(false);

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  // Calculate total number of items in cart
  const totalItemsInCart = cartItems.reduce((total, item) => {
    return total + item.quantity;
  }, 0);


  const addToCart = (productId, productData) => {
    console.log('Adding product to cart:', productId, productData);
    const existingProductIndex = products.findIndex((p) => p.id === productId);

    if (existingProductIndex !== -1) {
      // Product already exists in the cart, update quantity
      console.log('Product already exists in cart, updating quantity');
      setProducts((prevProducts) =>
        prevProducts.map((p, index) =>
          index === existingProductIndex ? { ...p, quantity: p.quantity + 1 } : p
        )
      );
    } else {
      // Product doesn't exist in the cart, add it
      console.log('Product not in cart, adding to cart');
      setProducts((prevProducts) => [
        ...prevProducts,
        { id: productId, data: productData, quantity: 1 },
      ]);
    }
  };



  const removeFromCart = (productId) => {
    console.log('Removing product from cart:', productId);
    const productIndex = products.findIndex((product) => product.id === productId);
    if (productIndex !== -1 && products[productIndex].quantity > 1) {
      // If product quantity is greater than 1, decrease it
      console.log('Decreasing quantity for product in cart');
      setProducts((prevProducts) => {
        const updatedProducts = [...prevProducts];
        updatedProducts[productIndex].quantity -= 1;
        return updatedProducts;
      });
    } else {
      // If product quantity is 1 or product not found, remove it from the cart
      console.log('Removing product completely from cart');
      setProducts((prevProducts) => prevProducts.filter((product) => product.id !== productId));
    }
  };

  return (
    <div className="">
      <div onClick={toggleCart} className={style.openClose}>
        {isOpen ? (
          <XIcon />
        ) : (
          <div>
            <ShoppingCartIcon />
            <p>{totalItemsInCart}</p>
          </div>
        )}
      </div>
      {isOpen && (
        <div className={style.CartContainer}>
          <h2>Shopping Cart</h2>
          <p>Total Items: {totalItemsInCart}</p>
          <ul className="">
            {cartItems.map((item, index) => (
              <li key={index} className={style.CartItems}>
                <PlusIcon /> {/* PlusIcon button */}
                <MinusIcon /> {/* MinusIcon button */}
                <span>{item.productData.title}</span>
                <span>${item.productData.price}</span>
                <span>Quantity: {item.quantity}</span>
                {item.productData.image && item.productData.image.url && (
                  <img src={item.productData.image.url} alt={item.productData.title} />
                )}
              </li>
            ))}


            <RoundedButton>
              <p>Go to checkOut</p>
            </RoundedButton>
          </ul>
        </div>
      )}
    </div>
  );
}

export default ShoppingCart;
