import React, { useState, useEffect } from 'react';
import Lottie from "lottie-react";
import Loading from '../../animations/Loadingskeleton.json';
import style from './style.module.css';
import RoundedButton from '../common/RoundedButton/RoundedButton';
import { motion, useAnimation } from 'framer-motion';

const apiUrl = process.env.REACT_APP_API_URL;
const words = "Todays offers ";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Set initial loading state to true
  const productAnimationControls = useAnimation(); // Animation controls for the products

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        console.log(data); // Log the response
        setProducts(data.data); // Assuming the products are under the 'data' key
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setTimeout(() => { // Add a setTimeout with a 1-second delay
          setLoading(false); // Set loading to false after the delay
          productAnimationControls.start("visible"); // Start product animation when loading completes
        }, 2000);  
      }
    };

    fetchProducts();
  }, [productAnimationControls]);

  return (
    <div className={style.Container}>
      {/* Animate the words and the h1 tag */}
      <motion.div
        className={style.BackgroundAnimation}
        initial={{ x: '100%' }}
        animate={{ x: '-100%', transition: { duration: 20, repeat: Infinity } }}
      >
        {words}
      </motion.div>

      {loading ? (
        <div className={style.Loader}>
          {[1, 2, 3].map((_, index) => (
            <div key={index} className={style.LoaderItem}>
              <Lottie animationData={Loading} width={20} height={20} />
            </div>
          ))}
        </div>
      ) : (
        <motion.div
          className={style.Products}
          initial={{ opacity: 0 }}
          animate={productAnimationControls}
        >
          {/* Rendering only the first three products */}
          {products.slice(0, 3).map((product, index) => (
            <motion.div 
              key={product.id} 
              className={style.Product}
              variants={{
                visible: { opacity: 1, y: 0 },
                hidden: { opacity: 0, y: 50 }
              }}
              initial="hidden"
              animate={productAnimationControls}
              transition={{ delay: index * 0.1 }} // staggered animation based on product index
            >
              <img src={product.image.url} alt={product.title} />
              <h3>{product.description}</h3>
              <p className={style.Old}>Price: ${product.price}</p>
              <span>New Price</span>
              <h2>${product.discountedPrice}</h2>
              <div className={style.Button}>
                <RoundedButton backgroundColor='#fff'><p>Add to cart</p></RoundedButton>
              </div>
            </motion.div>
          ))}
        </motion.div>
      )}
    </div>
  );
}
