// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Hero from './Components/Hero';
import HpProducts from './Components/HpProducts/HpProducts';
import AllProducts from './pages/AllProducts'; // Import the AllProducts component
import NotFound from './pages/NotFound';
import { AnimatePresence } from 'framer-motion';
import { useEffect } from 'react';
import Preloader from '../src/Components/Preloader/index';
import Nav from './Components/Nav';
import ProductDetails from './pages/products/[product.id]';
import ShoppingCart from './Components/Cart/ShoppingCart'; // Import the ShoppingCart component

function HomePage() {
  return (
    <>
      <Hero />
      <HpProducts />
    </>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default';
          window.scrollTo(0, 0);
        }, 1000);
      }
    )();
  }, []);

  // Function to add items to the cart
  const addItemToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>
        {/* Pass addItemToCart to Nav */}
        <Nav addItemToCart={addItemToCart} cartItems={cartItems}  />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/AllProducts" element={<AllProducts />} />
          {/* Pass addItemToCart function to ProductDetails */}
          <Route path="/products/:id" element={<ProductDetails addItemToCart={addItemToCart} />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
        {/* Render the ShoppingCart component */}

      </div>
    </Router>
  );
}

export default App;
