// Nav.js
import React, { useEffect, useState } from 'react';
import Styles from './style.module.css';
import { motion, useAnimation } from 'framer-motion';
import ShoppingCart from '../Cart/ShoppingCart';
import { Link } from 'react-router-dom';
const words = "NOROFF - ECOMMERCE";

export default function Nav({ addItemToCart, cartItems }) { // Receive addItemToCart and cartItems as props
  const [isLoading, setIsLoading] = useState(true);
  const controls = useAnimation();

  useEffect(() => {
    const startAnimation = () => {
      setTimeout(() => {
        setIsLoading(false);
        controls.start(i => ({
          opacity: 1,
          transition: { delay: i * 0.1 }
        }));
      }, 2000); // Delay of 2 seconds
    };

    if (isLoading) {
      startAnimation();
    }
  }, [isLoading, controls]);

  return (
    <div className={Styles.Nav}>
      <Link to="/">
        {words.split('').map((letter, index) => (
          <motion.span
            key={index}
            initial={{ opacity: 0 }}
            animate={controls}
            custom={index}
          >
            {letter}
          </motion.span>
        ))}
      </Link>
      {/* Pass cartItems down to ShoppingCart */}
      <div className={Styles.Cart}><ShoppingCart addItemToCart={addItemToCart} cartItems={cartItems} /></div>
    </div>
  );
}
