import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Hero from './Components/Hero';
import Products from './Components/Products';
import AllProducts from './pages/AllProducts/AllProducts/AllProducts.js'; // Import the AllProducts component
import NotFound from './pages/NotFound';
import { AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';
import Preloader from '../src/Components/Preloader/index';
import Nav from './Components/Nav';

function App() {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    (
      async () => {
        setTimeout(() => {
          setIsLoading(false);
          document.body.style.cursor = 'default';
          window.scrollTo(0, 0);
        }, 1000);
      }
    )();
  }, []);

  return (
    <Router>
      <div className="App">
        <AnimatePresence mode='wait'>
          {isLoading && <Preloader />}
        </AnimatePresence>
        <Nav />
        <Routes>
          {/* Render both Hero and Products components */}
          <Route path="/" element={<Home />} />
          {/* Define other routes */}
          <Route path="/AllProducts" element={<AllProducts />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
}

// Nested component rendering both Hero and Products
function Home() {
  return (
    <>
      <Hero />
      <Products />
    </>
  );
}

export default App;
