import React, { useState, useEffect, useRef } from 'react';
import Lottie from "lottie-react";
import Loading from '../animations/Loadingskeleton.json';
import style from '../pages/AllProducts/style.module.css'
import { motion, useAnimation } from 'framer-motion';
import RoundedButton from '../src/Components/common/RoundedButton/RoundedButton';
import { Link } from 'react-router-dom';

const apiUrl = process.env.REACT_APP_API_URL;
const words = "All Items:";

export default function AllProducts({ addItemToCart }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Set initial loading state to true
  const containerRef = useRef(null); // Reference to the container div
  const controls = useAnimation(); // Framer Motion controls

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("https://v2.api.noroff.dev/online-shop");
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        console.log(data); // Log the response
        setProducts(data.data); // Assuming the products are under the 'data' key
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    addItemToCart(product);
  };

  return (
    <motion.div ref={containerRef} className={style.Container}>
      <motion.div
        initial={{ opacity: 0 }}
        animate={controls}
        className={style.BackgroundAnimation}
      >
        {words.split('').map((letter, index) => (
          <motion.span
            key={index}
            initial={{ opacity: 0 }}
            animate={controls}
            custom={index}
          >
            {letter}
          </motion.span>
        ))}
      </motion.div>

      {loading ? (
        <div className={style.Loader}>
          {[1, 2, 3].map((_, index) => (
            <motion.div 
              key={index} 
              initial={{ opacity: 0 }} 
              animate={{ opacity: 1 }} 
              transition={{ duration: 0.5, delay: index * 0.1 + 1 }}
            >
              <Lottie animationData={Loading} width={20} height={20} style={{height: "500px", width: "auto", overflow: "auto"}}/>
            </motion.div>
          ))}
        </div>
      ) : (
        <div className={`${style.Products}`}>
          {products.map((product, index) => (
            <motion.div 
              key={product.id} 
              className={`${style.Product} ${style.GridProduct}`}
              variants={{
                visible: { opacity: 1, y: 0 },
                hidden: { opacity: 0, y: 50 }
              }}
              initial="hidden"
              animate={controls}
              transition={{ delay: index * 0.1 }}
            >
              <Link to={`/products/${product.id}`}> 
                <img src={product.image.url} alt={product.title} />
                <h3>{product.description}</h3>
                <p className={style.Old}>Price: ${product.price}</p>
                <span>New Price</span>
                <h2>${product.discountedPrice}</h2>
                <div className={style.Button}>
                  <RoundedButton backgroundColor='#fff' onClick={() => handleAddToCart(product)} type="submit">
                    <p>Add to cart</p>
                  </RoundedButton>
                </div>
              </Link>
            </motion.div>
          ))}
        </div>
      )}
    </motion.div>
  );
}
