import React, { useState } from 'react';
import { ShoppingCartIcon, XIcon, PlusIcon, MinusIcon } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion'; // Import motion components
import style from './style.module.css';
import RoundedButton from '../common/RoundedButton/RoundedButton';
import { Link } from 'react-router-dom';

function ShoppingCart({ cartItems, removeFromCart, addItemToCart }) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleCart = () => {
    setIsOpen(!isOpen);
  };

  const handleAddItem = (productId, productData) => {
    addItemToCart(productId, productData);
  };

  const handleRemoveItem = (index) => {
    removeFromCart(index);
  };

  const totalItemsInCart = cartItems.reduce((total, item) => {
    return total + item.quantity;
  }, 0);

  return (
    <>
      <motion.div
        className={style.BackgroundContainer}
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -50 }}
      >
        <div onClick={toggleCart} className={style.openClose}>
          {isOpen ? (
            <XIcon />
          ) : (
            <div>
              <ShoppingCartIcon />
              {/* Render the totalItemsInCart only when the cart is open */}
              {isOpen && <p>{totalItemsInCart}</p>}
            </div>
          )}
        </div>
      </motion.div>

      <AnimatePresence>
        {isOpen && (
          <motion.div
            className={style.CartContainer}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
          >
            <h2>Shopping Cart</h2>
            <p>Total Items: {totalItemsInCart}</p>
            <ul>
              {cartItems.map((item, index) => (
                <motion.li
                  key={index}
                  className={style.CartItems}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                >
                  <div>
                    <PlusIcon onClick={() => handleAddItem(item.productId, item.productData)} />
                    <MinusIcon onClick={() => handleRemoveItem(index)} />
                  </div>
                  <span>{item.productData.title}</span>
                  <span>${item.productData.discountedPrice}</span>
                  <span>Quantity: {item.quantity}</span>
                  {item.productData.image && item.productData.image.url && (
                    <img src={item.productData.image.url} alt={item.productData.title} />
                  )}
                </motion.li>
              ))}
              <Link to="/CheckOut">
                <RoundedButton backgroundColor='#fff'>
                  <p>Go to checkout</p>
                </RoundedButton>
              </Link>
            </ul>
          </motion.div>
        )}
      </AnimatePresence>
    </>
  );
}

export default ShoppingCart;
